// Define the function to be executed by the UI Action
function assignToDeveloper() {
  // Get the current record
  var current = this;
	alert("test");
  var currentRecord = new GlideRecord("x_982612_technical_incident");
  currentRecord.addQuery('sys_id', current.sys_id);
  currentRecord.query();
  if (currentRecord.next()) {

	 // Check if the u_assignment_group and u_assigned_to fields are empty
    var assignmentGroupValue = currentRecord.getValue("u_assignment_group");
    var assignedToValue = currentRecord.getValue("u_assigned_to");
    var fieldsEmpty = JSUtil.nil(assignmentGroupValue) && JSUtil.nil(assignedToValue);
      gs.log("anwar",assignedToValue);
    // Get the sys_id of the "Application Development" group from the sys_user_group table
    var assignmentGroupGR = new GlideRecord('sys_user_group');
    assignmentGroupGR.addQuery('name', 'Application Development');
    assignmentGroupGR.query();
    if (assignmentGroupGR.next()) {
        assignmentGroupValue = assignmentGroupGR.getUniqueValue();
    }

    // Get the sys_id of the currently logged in user from the v_user_session table
    var sessionGR = new GlideRecord('v_user_session');
    sessionGR.addQuery('user', gs.getUserID());
    sessionGR.query();
    if (sessionGR.next()) {
        assignedToValue = sessionGR.getValue('user');
    }

    // Update the u_assignment_group and u_assigned_to fields
   // if (fieldsEmpty)
	{
      currentRecord.u_assignment_group = assignmentGroupValue;
      currentRecord.u_assigned_to = assignedToValue;
    }

    // Set the value of the u_customer_state field to "Assigned"
    currentRecord.u_customer_state = "assigned";

    // Save the changes
   // current.update();
  }
}
